import { useState, useEffect } from "react";
import summaryData from "../json/summary.json";

export default function Summary(){
    const [isSummaryShow, setIsSummaryShow] = useState(true);
    const [data, setData] = useState(null);

    useEffect(() => {
        // Simulate fetching data from JSON
        setData(summaryData);
    }, []);

    return(
        <div>
            <div className='summaryBox'>
                <div className='summaryHead'>
                    <h3>Summary</h3>
                    <div onClick={()=>setIsSummaryShow(!isSummaryShow)}>{isSummaryShow ? 'Hide' : 'Show'}</div>
                </div>
                {isSummaryShow && data && 
                <div className='summaryContent'>
                    <p>Selected Vehicle: {data.summary.selectedVehicle}</p>
                    <p>Rental Duration: {data.summary.rentalDuration}</p>
                    <p>Total Cost: {data.summary.totalCost}</p>
                    <div className="truck-summary">
                        <h4>Truck Summary</h4>
                        <p>Total Trucks: {data.innerObject.allTruckWiseData[0].allTruckDetails[0]["Total Trucks"]}</p>
                        <p>Total Bags: {data.innerObject.allTruckWiseData[0].allTruckDetails[0]["Total Bags"]}</p>
                        <p>Gross Weight: {data.innerObject.allTruckWiseData[0].allTruckDetails[0]["Gross WT"]}</p>
                    </div>
                </div>
                }
            </div>
        </div>
    )
}
