import Counter from "../components/hooks/Counter";
import Stopwatch from "../components/hooks/StopWatch";
import { useEffect, useState } from "react";

export default function Home() {
    const[timer, setTimer] = useState(60)
    const[isTimerRunning, setIsTimerRunning] = useState(false)

    useEffect(()=>{
        let timerFunction;
        timerFunction = setInterval(()=>((timer)=>timer-1));
        return clearInterval(timerFunction)
    },[timer, isTimerRunning])

    return(
    <div>
        <li>Counter App</li>
        <Counter/>
        <li>Todo List ✓</li>
        <li>Search Filter ✓</li>
        <li>Tabs Component</li>
        <li>Stopwatch/Timer ✓</li>
        <Stopwatch/>

        {/* Forms & Validation */}
        <li>Form Validation</li>
        <li>Dynamic Form</li>
        <li>Multi-Step Form</li>
        {/* Lists & API Integration */}
        <li>Fetch API Data</li>
        <li>Pagination</li>
        <li>Infinite Scroll</li>
        <li>Sorting & Filtering</li>
        <li>Theme Switcher</li>
        <li>Cart System</li>
        <li>Global State</li>

    </div>
    );
}
