import { useState } from "react";

export default function Summery(){
    const[isSummaryShow, setIsSummaryShow] = useState('true')

    return(
    <div>
        <div className='summaryBox'>
            <div className='summaryHead'>
                <h3>Summary</h3>
                <button onClick={()=>setIsSummaryShow(!isSummaryShow)}>{isSummaryShow ? 'Hide' : 'Show'}</button>
            </div>
            {isSummaryShow && 
            <div className='summaryContent'>
                <p>Selected Vehicle: Tesla Model 3</p>
                <p>Rental Duration: 3 days</p>
                <p>Total Cost: $150</p>
            </div>
            }
        </div>
    </div>

    )

}
