import { useState } from "react";
import summaryJson from "../../src/json/Summary_Json.txt";

export default function VehicleDetails() {
    const [moreDetailsShow, setMoreDetailsShow] = useState('true')
    console.log(summaryJson.summary)
    // const [isSummaryShow, setIsSummaryShow] = useState(true);
    // const totalTrucks = summaryJson.innerObject.allTruckWiseData[0].allTruckDetails[0]["Total Trucks"];
    // console.log(totalTrucks, "totalTrucks");

    return (
        <div>
            <div className='vehicleDetailsBox'>
                <div className='vehicleDetailsHead'>
                    <h3>Vehicle Details</h3>
                    <div>

                        {/* <p>Total Trucks: {totalTrucks}</p> */}

                    </div>
                    <button onClick={() => setMoreDetailsShow(!moreDetailsShow)}>{moreDetailsShow ? 'Hide' : 'Show'}</button>
                </div>
                {moreDetailsShow &&
                    <div className='vehicleDetailsContent'>
                        <p>Make: Tesla</p>
                        <p>Model: Model 3</p>
                        <p>Year: 2020</p>
                    </div>
                }
            </div>
        </div>
    )

}
