import {useEffect, useState} from "react"

export default function Stopwatch() {
    //  User sets a time (e.g., 10 seconds).
    const [time, setTime] = useState(60)
    const [isTimerRunning, setIsTimerRunning] = useState(false)

    useEffect(()=>{
        let timer; //interval id
        if(isTimerRunning && time > 0){// check that the timer is not a 0 and time is not stopped
            timer = setInterval(()=>setTime((t)=>t-1),1000)
        }
        return()=>clearInterval(timer);
    },[time, isTimerRunning])

    // Starts countdown.
    // const handleStart = () => {
    //     time = Date.now()
    //     console.log(time)
    // }
        // Reset option.
    const handleReset = () => {
            setTime(60)
    }
    // // Stops at 0.
    return (
        <div>
            <button onClick={()=>{setIsTimerRunning(true)}}>Start:</button>
            <div> {time}</div><button onClick={handleReset}>reset</button>
            <button onClick={()=>{setIsTimerRunning(false)}}>stop</button>
        </div>
    )
}
